import Vue from 'vue'
import Router from 'vue-router'
import Hello from '../components/Hello.vue'
import page2 from '../components/page2'

Vue.use(Router)

export default new Router({
  routes: [
    {
      path: '/',
      name: 'Hello',
      component: Hello
    },
    {
      path: '/page2',
      name: 'page2',
      component: page2
    }
  ]
})
