<script>

export default {
 name: 'CarouselNav',
 props:{
  
  count:0,
  index : 0

 },
 
 beforeMount(){
 
 },
 mounted (){
  
 },
 beforeDestroy (){
   
 },
 data () {
    return {
    }
  },
  render (createElement){
    
      const opt = this.$props;
        if(opt.count <= 1){
            return (<span class="ui_indicator"></span>)
        }
        let items = [],
            actives = opt.index;
         for(let i = 0;i < opt.count; i++){
                items[i]= (<i key={'ui_indicator' + i} class={(i == actives ? 'indicator_active':"")}></i>)
            }
        return (
            <span class="ui_indicator">
            {
               items
            }
            </span>
        )
  
  },
  methods: {


  }
}
</script>


