<script>
import CarouselNav from './carousel-nav'
import CarouselList from './carousel-list'


export default {
 name: 'TcCarousel',
 props:{
    loop:{
      type:Boolean,
      default:true
    },
    speed:{
      type:Number,
      default:400
    },
    duration:{
      type:Number,
      default:3000
    },
    autoplay:{
      type:Boolean,
      default:true
    },
    nav:{
      type:Boolean,
      default:true
    },
    vertical:{
      type:Boolean,
      default:false
    },
    list:{
      type:Array,
      
    },
    carouselRef:{
      type:String,
       default:'carousecon'
    }
  

 },
 
 beforeMount(){
 
 },
 mounted (){

     this.tsliderWidth = this.getWidth(this.$refs[this.carouselRef])
 
        this.sliderWidth = this.tsliderWidth
        if(this.autoplay) { //初始化时候 自动执行幻灯
            this.start();
        }
 },
 beforeDestroy (){
   this.stop()
 },
 data () {
    return {
            index : 0, //当前真实页码
            lastIndex:0,
            parentStyle:{},
            sliderWidth:0,
            transformPosition:0,
            num:0  //当前位置页码 可能 》 index

    }
  },
  components:{
      "CarouselList": CarouselList,
      "CarouselNav":CarouselNav
    },
  watch:{
  },
  computed: {    
  },
  render (h){
    
     const  courses  = this;

        if(courses.list.length < 1){
            return ('')
        }
        // let childPros = {   
        //                     list:courses.list,
        //                     loop:courses.loop,
        //                     num:this.num,
        //                     index:this.index,
        //                     parentStyle:this.parentStyle,
        //                     startSlider:this.start,
        //                     stopSlider:this.stop,
        //                     updateState:this.updateState,
        //                     updateUlStyle:this.updateUlStyle,
        //                     transformPosition:this.transformPosition,
        //                     to:this.to
        //              };
        //              console.log({...childPros})
        return (
            <div class="ui_carousel" ref={courses.carouselRef}>
                <CarouselList 
                  list = {courses.list}
                  loop = {courses.loop}
                  num = {this.num}
                  index = {this.index}
                  parentStyle={this.parentStyle}
                            startSlider={this.start}
                            stopSlider={this.stop}
                            updateState={this.updateState}
                            updateUlStyle={this.updateUlStyle}
                            transformPosition={this.transformPosition}
                            to={this.to}
                 />
                <CarouselNav count={courses.list.length} index={this.index} />
            </div>
        )
  
  },
  methods: {
      getWidth: function getWidth(elem) {
        return elem.getBoundingClientRect().width || elem.offsetWidth;
      },
      getHeight(elem) {
          return elem.getBoundingClientRect().height || elem.offsetHeight;
      },
      start (){
        var self = this;
        if(this.autoPlayTimer){
            this.stop()
        }
        this.autoPlayTimer = setInterval(() => {
        
            this.to(self.num + 1);
        }, this.duration);
    },
    stop (){
        clearInterval(this.autoPlayTimer)
    },
    to (i) {
      
        this.stop();
        let lens = this.list.length;
        if(lens <= 1){
            return 
        }

        if(i < 0){
            this.updateindex({num:lens - 1,index:lens - 1,lastIndex:0});
        }else{
            this.updateindex({num:i,index:(i === this.list.length ? 0 : i),lastIndex: i - 1});
        }

        setTimeout(function () {
            if (i === this.list.length) {
                this.updateindex({num:0},true);
                i = 0;
            }
            if(this.autoplay) { //初始化时候 自动执行幻灯
                this.start();
            }
        }.bind(this),this.speed);
        
    },
    updateindex (data,stopAnim){
   
       var self = this;
        const styls = this.updateUlStyle(data,stopAnim);
        var obj = Object.assign(data, styls);
        for(var i in obj){
         
           self[i] = obj[i]
        }

      
    },
    updateState(data,fn){

    },
    updateUlStyle (data,stopAnims){
        const pros = this,
              states = this;
        let left = data.ulPositionOffset || (- data.num * this.sliderWidth),
            stopAnim = (states.num === pros.list.length && states.index === 0 && states.lastIndex !== pros.list.length - 1) || stopAnims;
       return {
            parentStyle : {
                WebkitTransform: !pros.vertical ? 'translate3d(' + left + 'px, 0px, 0px)' : 'translate3d(0px, ' + left + 'px, 0px)',
                transform: !pros.vertical ? 'translate3d(' + left + 'px, 0px, 0px)' : 'translate3d(0px, ' + left + 'px, 0px)',
                transition: 'transform '+ ( stopAnim ? 0: pros.speed) +'ms ease 0s',
                WebkitTransition: 'transform '+ (stopAnim ? 0: pros.speed) +'ms ease 0s',
                msTransform: !pros.vertical ? 'translateX(' + left + 'px)' : 'translateY(' + left + 'px)',
            },
            transformPosition:left
      }
    }

  }
}
</script>
<style>
ul,li{list-style: none;padding: 0;} 
.ui_carousel {
    position: relative;
    overflow: hidden;
}

.ui_carousel ul {
    position: relative;
    width: 100%;
    margin:0;
}
.ui_carousel li {
    position: absolute;
    width: 100%;
    left: 100%;
    top: 0
}
.ui_carousel li:first-child {
    position: relative;
    left: 0
}
.ui_carousel a > img {
    display: block;
    width: 100%
}

.ui_indicator {
    position: absolute;
    left: 50%;
    bottom: 10px;
    line-height: 0;
    padding: 2px 3px 2px 8px;
    border-radius: 5px;
    background: rgba(0, 0, 0, .3);
    transform: translateX(-50%);
    -webkit-transform: translateX(-50%);
}
.ui_indicator i {
    width: 6px;
    height: 6px;
    display: inline-block;
    border-radius: 50%;
    background: #fff;
    margin-right: 5px
}
.ui_indicator .indicator_active {
    background: #f60
}
</style>


